mapper.update:
UPDATE video_metadata
file_path = #{filePath}, file_name = #{fileName}, file_size = #{fileSize}, hash_value = #{hashValue}, file_extension = #{fileExtension}, episode_id = #{episodeId}, anime_id = #{animeId}, anime_title = #{animeTitle}, episode_title = #{episodeTitle}, type = #{type}, type_description = #{typeDescription}, shift = #{shift}, matched = #{matched}, join_time = #{joinTime}, is_air = #{isAir}
WHERE id = #{id}

mapper.select:
SELECT * FROM video_metadata
WHERE #{filePath}
LIKE substr(file_path, 1, LENGTH(file_path) - INSTR(REVERSE(file_path), '.')) || '%';
